# 13邮政编码
def get_postal_code(txt):
    last = len(txt)
    r1 = r'\D\d{6}\D'
    return_postal_code = ''
    temp = re.search(r1, txt)
    if temp != None:
        a, b = temp.span()
        k = a + 1
        while k < b - 1:
            return_postal_code = return_postal_code + txt[k]
            k = k + 1
    if return_postal_code == '':
        return_postal_code = '*'
    return return_postal_code


